<?php get_header(); ?>
	<div id="main">
		<?php is_tag(); ?>
			<?php if (have_posts()) : ?>
				<div class="page-title"><?php					if ( is_day() ) :						printf( __( 'Daily Archives: %s', 'sturd' ), '<span>' . get_the_date() . '</span>' );					elseif ( is_month() ) :						printf( __( 'Monthly Archives: %s', 'sturd' ), '<span>' . get_the_date( _x( 'F Y', 'monthly archives date format', 'sturd' ) ) . '</span>' );					elseif ( is_year() ) :						printf( __( 'Yearly Archives: %s', 'sturd' ), '<span>' . get_the_date( _x( 'Y', 'yearly archives date format', 'sturd' ) ) . '</span>' );					else :						_e( 'Archives', 'sturd' );					endif;				?></div>		<?php while (have_posts()) : the_post(); ?>			<div <?php post_class() ?> id="post-<?php the_ID(); ?>">				<div class="post-thumb">					<?php if ( has_post_thumbnail()) : ?>						<a href="<?php the_permalink() ?>" title="<?php the_title(); ?>">							<?php the_post_thumbnail('big-thumb', array('title' => '')); ?>						</a>					<?php endif; ?>				</div>
				<div class="post-entry">					<h2 class="post-title">						<a href="<?php the_permalink() ?>" rel="bookmark" title="<?php the_title_attribute(); ?>"><?php the_title(); ?></a>					</h2>					<div class="post-meta">						<a href="<?php the_permalink() ?>"><?php the_time(get_option('date_format')); ?></a> <?php _e( 'by', 'sturd' ); ?> <?php the_author() ?> | <?php _e( 'Filed in', 'sturd' ); ?> <?php the_category(', ') ?> | <?php comments_popup_link('No comments', '1 comment', '% comments'); ?>					</div>					<div class="entry">					<?php the_excerpt(''); ?>					</div>				</div>			</div>		<?php endwhile; ?>		<div class="navigation">			<div class="a-left"><?php next_posts_link('&laquo; Older Entries') ?></div>			<div class="a-right"><?php previous_posts_link('Newer Entries &raquo;') ?></div>			<div class="clear"></div>		</div>		<?php else : ?>			<div class="page-title"><?php _e( 'Not Found', 'sturd' ); ?></div>		<?php endif; ?>	</div><?php get_sidebar(); ?><div class="clear"></div><?php get_footer(); ?>